from tkinter import *
from tkinter import filedialog
import pygame
import base64
pygame.mixer.init()
def open_file():
    file_path=filedialog.askopenfilename(filetypes=[("Audio Files","*.mp3 *.wav *.ogg")])
    if file_path:
        pygame.mixer.music.load(file_path)
        pygame.mixer.music.play()
        track.config(text=file_path.split("/"[-1]))
def play_music():
    pygame.mixer.music.unpause()
def pause_music():
    pygame.mixer.music.pause()
def stop_music():
    pygame.mixer.music.stop()
    track.config(text="")
root=Tk()
root.title("audio player")
root.geometry("480x168")
root.minsize(480,168)
root.resizable(width=False,height=True)
#yee i make photo in text lmao
icon_data = b""
icon_image = PhotoImage(data=base64.b64decode(icon_data))

menu_bar = Menu(root)
file_menu = Menu(menu_bar,tearoff=0)
file_menu.add_command(label="Open",command=open_file)
file_menu.add_separator()
file_menu.add_command(label="Exit",command=root.quit)
menu_bar.add_cascade(label="File",menu=file_menu)
root.config(menu=menu_bar)

track=Label(root,text="",width=100)
track.pack(pady=20)
button_fr=Frame(root)
button_fr.pack(pady=10)
play_button = Button(button_fr, text="Play", width=10, command=play_music)
play_button.grid(row=0, column=0, padx=5)

pause_button = Button(button_fr, text="Pause", width=10, command=pause_music)
pause_button.grid(row=0, column=1, padx=5)

stop_button = Button(button_fr, text="Stop", width=10, command=stop_music)
stop_button.grid(row=0, column=2, padx=5)
root['bg']='grey'
root.iconphoto(True, icon_image)
root.mainloop()